using RubricaTelefonicaAziendale.Entities;

namespace RubricaTelefonicaAziendale.Dtos
{
    public class UserDto
    {
        private static readonly String DefaultPicture = "data:image/webp;base64,";


        public String Id { get; set; } = String.Empty;
        public String? Firstname { get; set; }
        public String? Lastname { get; set; }
        public String Username { get; set; } = String.Empty;
        public String Password { get; set; } = String.Empty;
        public String? Picture { get; set; }
        public String RoleId { get; set; } = String.Empty;
        public String Role { get; set; } = String.Empty;

        public static UserDto ConvertToDto(Users obj, Roles? role)
        {
            return new UserDto()
            {
                Id = obj.Id,
                Firstname = obj.Firstname,
                Lastname = obj.Lastname,
                Username = obj.Username,
                Picture = obj.Picture ?? DefaultPicture,
                RoleId = role?.Id ?? String.Empty,
                Role = role?.Description ?? String.Empty
            };
        }

        public static Users ConvertToEntity(UserDto obj, Users dbobj)
        {
            dbobj.Firstname = obj.Firstname ?? String.Empty;
            dbobj.Lastname = obj.Lastname ?? String.Empty;
            dbobj.Username = obj.Username;
            dbobj.Password = obj.Password;
            dbobj.Picture = obj.Picture;
            return dbobj;
        }
    }
}